mapper.select:
SELECT id, requester_id, receiver_id, status, created_at, accepted_at
FROM friend
WHERE requester_id = #{userId}

mapper.update:
UPDATE friend
SET status = 'AUTO_CANCELLED'
WHERE requester_id = #{oppositeRequesterId}
  AND receiver_id = #{oppositeReceiverId}
  AND status = 'PENDING'

mapper.select:
SELECT id, requester_id, receiver_id, status, created_at, accepted_at
FROM friend
WHERE id = #{friendId}
