mapper.update:
UPDATE users
SET nickname = #{newNickName}
WHERE id = #{userId}

UPDATE users
SET password = #{newPassword}
WHERE id = #{userId}

UPDATE users
SET is_Active = #{isActive}
WHERE id = #{userId}

mapper.select:
SELECT email
FROM users
WHERE username = #{name}
  AND phone_number = #{phoneNumber}

SELECT email, id
FROM users
WHERE username = #{name}
  AND email = #{email}
