mapper.select:
SELECT *
FROM user_team
WHERE user_id = #{userId}
  AND status = '가입';

SELECT *
FROM user_team
WHERE user_id = #{userId}
AND status = '대기';

SELECT *
FROM user_team
WHERE team_id = #{teamId}
  AND user_id = #{userId}

mapper.update:
UPDATE user_team
SET status = '가입'
WHERE team_id = #{teamId}
  AND user_id = #{userId}

mapper.delete:
DELETE FROM user_team
WHERE team_id = #{teamId}
AND user_id = #{userId}
